export default class extends React.Component {

  static async getInitialProps({ query }) {
    let idChannel = query.id

    let reqChannel = await fetch(`https://api.audioboom.com/channels/${idChannel}`)
    let { body: { channel } } = await reqChannel.json()

    let reqAudio = await fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
    let { body: { audio_clips: audioClips } } = await reqAudio.json()

    let reqSeries = await fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
    let { body: { channels: series } } = await reqSeries.json()


    return { channel, audioClips, series }
  }

  render() {
    const  { channel, audioClips, series } = this.props

    return <> 
      <header>Podcast</header>
      <h1>{ channel.title }</h1>

      { series.map((serie) => (
        <div>{ serie.title }</div>
      ))}

      { audioClips.map((clip) => (
        <div>{ clip.title }</div>
      ))}

      <style jsx>{`
        header {
          color: #fff;
          background: #8756ca;
          padding: 15px;
          text-align: center;
        }

        .channels {
          display: grid;
          grid-gap: 15px;
          padding: 15px;
          grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
        }

        .channel img {
          border-radius: 3px;
          box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
          width: 100%;
        }
        h1 {
          font-weiht: 600px;
          padding: 15px
        }
        h2 {
          padding: 5px;
          font-size: 0.9em;
          font-weight: 600;
          margin: 0;
          text-align: center;
        }
      `}</style>

      <style jsx global>{`
        body {
          margin: 0;
          font-family: system-ui;
          backgound: white;
        }  
      `}</style>
    </>
  }


}
