import Link from 'next/link'
import Layout from '../components/Layout'
import VolverLink from '../components/VolverLink'

export default class extends React.Component {
	static async getInitialProps({ query }) {
		let idPodcast = query.id

		let reqPodcast = await fetch(`https://api.audioboom.com/audio_clips/${idPodcast}.mp3`)
		let { body: { audio_clip: clip }} = await reqPodcast.json()

		return { clip }
	}

	render() {
		let { clip } = this.props

		return <Layout title={ `Podcast | ${ clip.title }` }>
			<div className='modal'>				
				<div className='clip'>
          <VolverLink clip={clip}/>

					<picture>
						<div style={{ backgroundImage: `url(${clip.urls.image || 
							clip.channel.urls.logo_image.original})` }} />
					</picture>

					<div className='player'>
							<h3>{ clip.title }</h3>
							<h6>{ clip.channel.title }</h6>
							<audio controls autoPlay={true}>
								<source src={clip.urls.high_mp3} type='audio/mpeg' />
							</audio>
					</div>
				</div>
			</div>

			<style jsx>{`
			header {
          color: #fff;
          background: #8756ca;
          padding: 15px;
          text-align: center;
        }

        .clip {
          display: flex;
          height: 100%;
          flex-direction: column;
          background: #8756ca;
          color: white;
        }
        picture {
          display: flex;
          align-items: center;
          justify-content: center;
          flex: 1 1;
          flex-direction: column;
          width: auto;
          padding: 10%;
        }
        picture div {
          width: 100%;
          height: 100%;
          background-position: 50% 50%;
          background-size: contain;
          background-repeat: no-repeat;
        }
        .player {
          padding: 30px;
          background: rgba(0,0,0,0.3);
          text-align: center;
        }
        h3 {
          margin: 0;
        }
        h6 {
          margin: 0;
          margin-top: 1em;
        }
        audio {
          margin-top: 2em;
          width: 100%;
        }

        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 99999;
        }
      `}</style>

      <style jsx global>{`
        body {
          margin: 0;
          font-family: system-ui;
          background: white;
        }
      `}</style>
    </Layout>
	} 
}
